import { styled } from '@mui/material/styles';
import ImagePreviewCard from '../../component/previewImageCard';
import FooterBookCard from '../../component/footerBookCard';
import { useEffect, useState } from 'react';

const Book = styled('div')(({ theme }) => ({
    borderRadius: '10px',
    boxShadow: '0 0 10px rgb(229 229 229 / 80%)',
    height: '100%'
}));

const BookContainer = styled('div')(({ theme }) => ({
    width: '200px',
    height: '40ch',
    padding: '10px 8px'
}));

function BookLayout(props) {
    const [isFavorite, setIsFavorite] = useState(props.action)
    const [isWishlist, setIsWishlist] = useState(props.action)

    useEffect(() => {
        setIsWishlist(false)
        props.setAction(false)
    }, [isFavorite])
    
    useEffect(() => {
        setIsFavorite(false)
        props.setAction(false)
    }, [isFavorite])
    
    return (
        <BookContainer>
            <Book>
                <ImagePreviewCard subtitle={props.obj.volumeInfo.title} thumbnail={props.obj.volumeInfo.imageLinks ? props.obj.volumeInfo.imageLinks.thumbnail : null} />
                <FooterBookCard authors={props.obj.volumeInfo.authors ? props.obj.volumeInfo.authors : []} title={props.obj.volumeInfo.title} isWishlistClicked={props.isWishlistClicked} isFavoriteClicked={props.isFavoriteClicked}
                    isWishlist={isWishlist} isFavorite={isFavorite} setIsFavorite={setIsFavorite} setIsWishlist={setIsWishlist}
                />
            </Book>
        </BookContainer>
    )
}

export default BookLayout
