import FavoriteIconDefault from '@mui/icons-material/FavoriteBorderOutlined'
import FavoriteIcon from '@mui/icons-material/FavoriteOutlined'
import WishlistIconDefault from '@mui/icons-material/BookmarkBorderOutlined'
import WishlistIcon from '@mui/icons-material/BookmarkOutlined'
import { pink, grey } from '@mui/material/colors';
import { Box } from '@mui/material'
import React, { useEffect } from 'react'

function WishlistBookCard(props) {

    const [isFavorite, setIsFavorite] = React.useState(false)
    const [isWishlist, setIsWishlist] = React.useState(false)

    useEffect(() => {
        setIsFavorite(false)
        setIsWishlist(false)
    }, [])

    return (
        <Box sx={{
            display: 'flex'
        }}>
            <Box>
                {isWishlist ? <WishlistIcon sx={{ color: grey[700] }} /> : <WishlistIconDefault onClick={() => { setIsWishlist(true); props.isWishlistClicked() }} />}
            </Box>
            <Box>
                {isFavorite ? <FavoriteIcon sx={{ color: pink[500] }} /> : <FavoriteIconDefault onClick={() => { setIsFavorite(true); props.isFavoriteClicked() }} />}
            </Box>
        </Box>
    )

}

export default WishlistBookCard
