import * as React from 'react';
import { Alert, Snackbar, Typography, Box, CardMedia } from '@mui/material'
import { getBooks } from '../../../function/apiGetBooks'
import SearchLayout from '../../layout/searchLayout';
import { styled, alpha } from '@mui/material/styles';

const CardList = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    margin: theme.spacing(4),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(2),
        width: 'auto',
    },
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center'
}));

const Book = styled('div')(({ theme }) => ({
    borderRadius: '10px',
    boxShadow: '0 0 10px rgb(229 229 229 / 80%)',
    height: '100%'
}));

const BookContainer = styled('div')(({ theme }) => ({
    width: '200px',
    height: '40ch',
    padding: '10px 8px'
}));

function BookCard() {
    const [value, setValue] = React.useState('')
    const [listBooks, setListBooks] = React.useState(null)
    const [timer, setTimer] = React.useState(null)
    const [state, setState] = React.useState({
        opens: false,
        message: null,
        severity: null
    });

    const { opens, message, severity } = state;

    const handleCloses = () => {
        setState({ ...state, opens: false });
    };

    function onChange(event) {
        setValue(event.target.value)
    }

    function findBooksWithKeywords(value) {
        if (value.length > 0) {
            getBooks(value).then(res => {
                if (res) {
                    setListBooks(res)
                } else {
                    setState({
                        opens: true,
                        message: "Kami tidak menemukan buku dengan kata kunci tersebut.",
                        severity: "error"
                    })
                }
            })
        }
    }

    const inputChanged = (event) => {
        clearTimeout(timer)

        const newTimer = setTimeout(() => {
            findBooksWithKeywords(event.target.value)
        }, 600)

        setTimer(newTimer)
    }

    return (
        <>
            <Snackbar anchorOrigin={{ vertical: 'top', horizontal: 'center', color: '#FFF' }} open={opens} autoHideDuration={2500} onClose={handleCloses}>
                <Alert onClose={handleCloses} severity={severity} sx={{ width: '350px', fontSize: '12px', fontFamily: 'Avenir' }}>
                    {message}
                </Alert>
            </Snackbar>
            <SearchLayout onChange={onChange} inputChanged={inputChanged} findBooksWithKeywords={findBooksWithKeywords} />
            <CardList>
                {listBooks && listBooks.map(obj =>
                    <BookContainer>
                        <Book>
                            <Box sx={{
                                width: '160px',
                                height: '245px',
                                margin: 'auto',
                            }}>
                                <CardMedia alt={obj.volumeInfo.subtitle} component="img" src={obj.volumeInfo.imageLinks.thumbnail} />
                            </Box>
                            <Box sx={{
                                padding: '0 4px',
                                whiteSpace: "nowrap",
                                overflow: "hidden",
                                textOverflow: "ellipsis",
                            }}>
                                {obj.volumeInfo.authors.map((name, i) =>
                                    <span style={{
                                        fontSize: "12px",
                                        fontWeight: 400,
                                        fontStyle: "normal",
                                        fontStretch: "normal",
                                        letteSpacing: "normal",
                                        lineHeight: '12px',
                                        color: "#6d6d6d",
                                        whiteSpace: "nowrap",
                                        overflow: "hidden",
                                        textOverflow: "ellipsis",
                                    }}>{(i ? ', ' : '') + name}</span>
                                )}
                            </Box>
                            <Box sx={{
                                padding: '0 4px',
                                whiteSpace: "nowrap",
                                overflow: "hidden",
                                textOverflow: "ellipsis",
                            }}>
                                <span>
                                    {obj.volumeInfo.title}
                                </span>
                            </Box>
                        </Book>
                    </BookContainer>
                )}
            </CardList>
        </>
    )
}

export default BookCard
