import FavoriteIconDefault from '@mui/icons-material/FavoriteBorderOutlined'
import FavoriteIcon from '@mui/icons-material/FavoriteOutlined'
import WishlistIconDefault from '@mui/icons-material/BookmarkBorderOutlined'
import WishlistIcon from '@mui/icons-material/BookmarkOutlined'
import { pink, grey } from '@mui/material/colors';
import { Box } from '@mui/material'
import React, { forwardRef, useEffect, useImperativeHandle } from 'react'

const WishlistBookCard = forwardRef((props, ref) => {

    const [isFavorite, setIsFavorite] = React.useState(props.isFavorite)
    const [isWishlist, setIsWishlist] = React.useState(props.isWishlist)

    const cleanValue = () => {
        setIsFavorite(false);
        setIsWishlist(false)
    };

    useImperativeHandle(ref, () => {
        return {
            setIsFavorite: cleanValue
        }
    });

    return (
        <Box sx={{
            display: 'flex'
        }}>
            <Box>
                {props.isWishlist ? <WishlistIcon sx={{ color: grey[700] }} /> : <WishlistIconDefault onClick={() => { props.setIsWishlist(true); props.isWishlistClicked() }} />}
            </Box>
            <Box>
                {props.isFavorite ? <FavoriteIcon sx={{ color: pink[500] }} /> : <FavoriteIconDefault onClick={() => { props.setIsFavorite(true); props.isFavoriteClicked() }} />}
            </Box>
        </Box>
    )

})

export default WishlistBookCard
