import * as React from 'react';
import { Alert, CardMedia, Snackbar } from '@mui/material'
import { getBooks } from '../../../function/apiGetBooks'
import SearchLayout from '../../layout/searchLayout';
import { styled } from '@mui/material/styles';
import ImagePreviewCard from '../previewImageCard';
import FooterBookCard from '../footerBookCard';
import empty from '../../../gif/empty.gif'

const CardList = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    margin: theme.spacing(4),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(2),
        width: 'auto',
    },
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center'
}));

const Book = styled('div')(({ theme }) => ({
    borderRadius: '10px',
    boxShadow: '0 0 10px rgb(229 229 229 / 80%)',
    height: '100%'
}));

const BookContainer = styled('div')(({ theme }) => ({
    width: '200px',
    height: '40ch',
    padding: '10px 8px'
}));

function BookCard() {
    const [value, setValue] = React.useState('')
    const [listBooks, setListBooks] = React.useState(null)
    const [timer, setTimer] = React.useState(null)
    const [state, setState] = React.useState({
        opens: false,
        message: null,
        severity: null
    });

    const { opens, message, severity } = state;

    const handleCloses = () => {
        setState({ ...state, opens: false });
    };

    function onChange(event) {
        setValue(event.target.value)
    }

    function findBooksWithKeywords(value) {
        if (value.length > 0) {
            getBooks(value).then(res => {
                if (res) {
                    setListBooks(res)
                    console.log(res)
                } else {
                    setState({
                        opens: true,
                        message: "Kami tidak menemukan buku dengan kata kunci tersebut.",
                        severity: "error"
                    })
                }
            }).catch(e => (
                setListBooks(null)
            ))
        }
    }

    const inputChanged = (event) => {
        clearTimeout(timer)

        const newTimer = setTimeout(() => {
            findBooksWithKeywords(event.target.value)
        }, 600)

        setTimer(newTimer)
    }

    const isWishlistClicked = () => {
        setState({ ...state, opens: false });
        setTimeout(() => {
            setState({
                opens: true,
                message: "Buku ini berhasil ditambahkan ke wishlist Anda.",
                severity: "success"
            })
        }, 100);
    }

    const isFavoriteClicked = () => {
        setState({ ...state, opens: false });
        setTimeout(() => {
            setState({
                opens: true,
                message: "Buku ini berhasil ditambahkan menjadi buku favorit Anda.",
                severity: "success"
            })
        }, 100);
    }

    var data = [
        {
            name: 'evan',
            point: 50000,
            grade: 'D'
        },
        {
            name: 'jefry',
            point: 70000,
            grade: 'C'
        },
        {
            name: 'rizky',
            point: 30000,
            grade: 'D'
        },
        {

            name: 'hanson',
            point: 10000,
            grade: 'B'
        },
        {

            name: 'candra',
            point: 30000,
            grade: 'A'
        },
        {
            name: 'goklas',
            point: 20000,
            grade: 'A'
        },
        {
            name: 'hendra',
            point: 20000,
            grade: 'B'
        },
        {

            name: 'surya',
            point: 30000,
            grade: 'A'
        }
    ]

    data.sort(
        function (a, b) {
            if (a.grade === b.grade) {
                // order point by desc if grade are same
                return b.point - a.point;
            }
            return a.grade > b.grade ? 1 : -1;
        }
    );

    var names = data.map(function (item) {
        return item['name'];
    });

    console.log(names);

    function print2largest(arr, arr_size) {
        let i;
        let largest = 0
        let second = null;

        if (arr_size < 2) {
            return null
        }

        // findlargestElement
        for (i = 0; i < arr_size; i++) {
            if (arr[i] > largest) {
                largest = arr[i];
            }
        }

        // findSecondLargestElement
        for (i = 0; i < arr_size; i++) {
            if (arr[i] > second && arr[i] < largest) {
                second = arr[i];
            }
        }

        return second
    }

    var listNumbers = [12, 5, 7, 17, 8, 0, -1, 16, 17]

    console.log(print2largest(listNumbers, listNumbers.length))

    function check_palindrome(str) {
        let j = str.length - 1;
        for (let i = 0; i < j / 2; i++) {
            let x = str[i]; // forward character
            let y = str[j - i]; // backward character
            if (x != y) {
                return 'not palindrom';
            }
        }

        return 'palindrom';

    }

    let test = "neveroddoreven";
    console.log(check_palindrome(test))

    return (
        <>
            <Snackbar anchorOrigin={{ vertical: 'top', horizontal: 'center', color: '#FFF' }} open={opens} autoHideDuration={2500} onClose={handleCloses}>
                <Alert onClose={handleCloses} severity={severity} sx={{ width: '350px', fontSize: '12px', fontFamily: 'Avenir' }}>
                    {message}
                </Alert>
            </Snackbar>
            <SearchLayout onChange={onChange} inputChanged={inputChanged} findBooksWithKeywords={findBooksWithKeywords} />
            <CardList>
                {listBooks && listBooks.map(obj =>
                    <BookContainer>
                        <Book>
                            <ImagePreviewCard subtitle={obj.volumeInfo.title} thumbnail={obj.volumeInfo.imageLinks ? obj.volumeInfo.imageLinks.thumbnail : null} />
                            <FooterBookCard authors={obj.volumeInfo.authors ? obj.volumeInfo.authors : []} title={obj.volumeInfo.title} isWishlistClicked={isWishlistClicked} isFavoriteClicked={isFavoriteClicked} />
                        </Book>
                    </BookContainer>
                )}
            </CardList>
            {!listBooks &&
                <CardMedia alt="empty page" component="img" src={empty} sx={{
                    width: '600px',
                    height: 'auto',
                    margin: 'auto'
                }} />
            }
        </>
    )
}

export default BookCard
