import * as React from 'react';
import { styled, alpha } from '@mui/material/styles';
import { InputBase, Toolbar, Alert, Slide, Snackbar } from '@mui/material'
import SearchIcon from '@mui/icons-material/SearchOutlined';
import { getBooks } from '../../../function/apiGetBooks';

function TransitionRight(props) {
    return <Slide {...props} direction="left" />;
}

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.black, 0.15),
    '&:hover': {
        backgroundColor: alpha(theme.palette.common.black, 0.25),
    },
    margin: theme.spacing(2),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(3),
        width: 'auto',
    },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
            width: '20ch',
        },
    },
}));

function BookCard() {
    const [value, setValue] = React.useState('')
    const [listBooks, setListBooks] = React.useState(null)
    const [state, setState] = React.useState({
        opens: false,
        message: null,
        severity: null
    });

    const { opens, message, severity } = state;

    const handleCloses = () => {
        setState({ ...state, opens: false });
    };

    function onChange(event) {
        setValue(event.target.value)
    }

    function findBooksWithKeywords() {
        getBooks(value).then(res => {
            if (res) {
                setListBooks(res.content)
            } else {
                setState({
                    opens: true,
                    message: "Harap mencoba beberapa saat lagi.",
                    severity: "error"
                })
                setTimeout(() => {
                    history.replace({
                        pathname: "/"
                    })
                }, 300);
            }
        })
    }

    return (
        <>
            <Snackbar anchorOrigin={{ vertical: 'top', horizontal: 'center', color: '#FFF' }} open={opens} autoHideDuration={2500} onClose={handleCloses}>
                <Alert onClose={handleCloses} severity={severity} sx={{ width: '350px', fontSize: '12px', fontFamily: 'Avenir' }}>
                    {message}
                </Alert>
            </Snackbar>
            <Toolbar>
                <Search>
                    <SearchIconWrapper>
                        <SearchIcon />
                    </SearchIconWrapper>
                    <StyledInputBase
                        placeholder="Searchâ€¦"
                        inputProps={{ 'aria-label': 'search' }}
                        name='book-search'
                        onChange={(event) => onChange(event)}
                    />
                </Search>
            </Toolbar>
        </>
    )
}

export default BookCard
