import { baseurl, getbooks } from "../../utility/Uri";

export const getBooks = (parameter) => {
    console.log(`${baseurl+getbooks}?q=${parameter}`)
    return fetch(`${baseurl+getbooks}?q=${parameter}`), {
        method: 'GET'
    }
    .then(response => response.json())
    .then((res) => {
        if (res) {
            return res.items
        }
    })
    .catch((e) => {
        return null
    })
}
